import React,{useEffect, useState} from 'react'
import { useNavigate } from 'react-router-dom'
import './Home.css'

export default function Home() {
  const [data, setData] = useState([])
  const [comment, setComment] = useState("")
  const [show, setShow] = useState(false)
  const [item, setItem] = useState([])
  const navigate = useNavigate()

  useEffect(()=> {
    const jwt_token = localStorage.getItem("token")
    if(!jwt_token){
      navigate("./signup")
    }

    const fetchData = async () => {
      const token = localStorage.getItem("token")
      try{const res = await fetch("http://localhost:3005/allPosts",{
        method:"GET",
        headers: {
          "Authorization": `Bearer ${token}`
        }
      })
      const data = await res.json()
      setData(data)
    }catch(err){
      console.log(err)
    }
    }
    fetchData()
  
  },[])

  const likePost = async (id)=> {
    const response = await fetch("http://localhost:3005/like",{
      method:'PUT',
      headers:{
        'Content-Type':"application/json",
        Authorization: "Bearer " + localStorage.getItem("token")
      },
      body: JSON.stringify({
        postId : id
      })
    })
    const result = await response.json()
    const newData = data.map((posts) => {
      if (posts._id == result._id){
        return result
      }else{
        return posts
      }
    })
    setData(newData)
  }

  const unlikePost = async (id)=> {
    const response = await fetch("http://localhost:3005/unlike",{
      method:'PUT',
      headers:{
        'Content-Type':"application/json",
        Authorization: "Bearer " + localStorage.getItem("token")
      },
      body: JSON.stringify({
        postId : id
      })
    })
    const result = await response.json()
    const newData = data.map((posts) => {
      if (posts._id == result._id){
        return result
      }else{
        return posts
      }
    })
    setData(newData)
  }

  const makeComment = async (id,commented) => { 
    try{const response = await fetch("http://localhost:3005/comment",{
      method: 'PUT',
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("token") 
      },
      body: JSON.stringify({
        text: commented,
        postId: id
      })
    })
    const commentData = await response.json()
    console.log(commentData)
  }catch(err) {
    console.log(err)
  }
  }


  return (
    <div className="home">
      {
        data.map((eachPost) => {
          return (
            <div className="cardd">
            <div className="card-header">
              <div className="card-pic">
                <img src="https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8cGVyc29ufGVufDB8MXwwfHx8MA%3D%3D" alt="profile" />
              </div>
              <h5>{eachPost.postedBy.name}</h5>
            </div>
            <div className="card-image">
              <img src={eachPost.photo} alt="photo" />
            </div>
            <div className="card-content">
              <div className="spanandlike">
                {
                  eachPost.likes.includes(JSON.parse(localStorage.getItem("user"))._id)? (
                    <span class="material-symbols-outlined material-symbols-outlined-red" onClick={() => {unlikePost(eachPost._id)}}>
                    favorite
                  </span>
                  ):(
                    <span class="material-symbols-outlined" onClick={() => {likePost(eachPost._id)}}>
                    favorite
                  </span>
                  )
                }
              <p>{eachPost.likes.length} Likes</p>
              </div>
            <p>{eachPost.body}</p>
            <p style={{fontWeight:"bolder", cursor:"pointer"}} onClick={() => {setShow(true),setItem(eachPost)}}>View all comments</p>
            <div className="add-comment">
            <span class="material-symbols-outlined">
               mood
            </span>
            <input type="text" placeholder='Add a comment' value={eachPost.comment} onChange={(e) => {setComment(e.target.value)}} />
            <button className="comment" onClick={()=> {makeComment(eachPost._id,comment)}}>post</button>
            </div>
            </div>
          </div>
          )
        })
      }
      {
        show && (
      <div className="showComment">
        <div className="container">
          <div className="postPic">
            <img src="http://res.cloudinary.com/kamleshcloud/image/upload/v1714903973/wfd76zlawjmqvfkrodmt.jpg" alt="" />
          </div>
          <div className="details">
          <div className="card-header" style={{borderBottom: "1px solid #00000029"}}>
              <div className="card-pic">
                <img src="https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8cGVyc29ufGVufDB8MXwwfHx8MA%3D%3D" alt="profile" />
              </div>
              <h5>kamlesh</h5>
            </div>
            <div className="comment-section" style={{borderBottom: "1px solid #00000029"}}>
              <p className="comm">
                <span className="commentor" style={{fontWeight:"bolder"}}>Ramesh </span>
                <span className="commentText">Awesome pic</span>
              </p>
              <p className="comm">
                <span className="commentor" style={{fontWeight:"bolder"}}>Ramesh </span>
                <span className="commentText">Awesome pic</span>
              </p>
              <p className="comm">
                <span className="commentor" style={{fontWeight:"bolder"}}>Ramesh </span>
                <span className="commentText">Awesome pic</span>
              </p>
              <p className="comm">
                <span className="commentor" style={{fontWeight:"bolder"}}>Ramesh </span>
                <span className="commentText">Awesome pic</span>
              </p>
            </div>
            <div className="card-content">
              <p>2likes</p>
              <p>awesome post</p>
              <div className="add-comment">
            <span class="material-symbols-outlined">
               mood
            </span>
            <input type="text" placeholder='Add a comment'  />
            <button className="comment" /*onClick={()=> {makeComment(eachPost._id,comment)}}*/>post</button>
            </div>
            </div>
          </div>
        </div>
        <div className="close-comment" onClick={() => {setShow(false)}}>
        <span class="material-symbols-outlined material-symbols-outlined-comment ">
            close
          </span>
        </div>
      </div>
        )
      }
      </div>
  )
}
