import React from 'react'
import { useState , useEffect} from 'react';
import '../css/CreatePost.css'
import { useNavigate } from 'react-router-dom';
import {toast } from 'react-toastify'

export default function CreatePost() {
    const navigate = useNavigate()
    const [body, setBody] = useState("")
    const [image, setImage] = useState("")
    const [Url, setUrl] = useState("")
    const token = localStorage.getItem("token")

    const loadFile = event  => {
      var output = document.getElementById('output');
      output.src = URL.createObjectURL(event.target.files[0]);
      output.onload = function() {
      URL.revokeObjectURL(output.src)
    }
    }
  
//Toast functions
const notifyA = msg => toast.error(msg, {
  position: "top-right",
  autoClose: 5000,
  hideProgressBar: false,
  closeOnClick: true,
  pauseOnHover: true,
  draggable: true,
  progress: undefined,
  theme: "dark",
  });

const notifyB = msg => toast.success(msg, {
  position: "top-right",
  autoClose: 5000,
  hideProgressBar: false,
  closeOnClick: true,
  pauseOnHover: true,
  draggable: true,
  progress: undefined,
  theme: "dark",
  });
  
  useEffect(() => {
    const uploadImg = async () => {
      if(Url){
        try {const res = await fetch("/createPost",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
          },
          body: JSON.stringify({
            body,
            pic:Url
          })
        }
      )
      const resData = await res.json()
      if(resData.message){
        notifyB(resData.message)
        navigate("/")
      }else{
        notifyA(resData.error)
      }
    }catch(err){
      console.log(err)
    }
      }
    }
    uploadImg()
},[Url])

  const postDetails =async () => {
    const data = new FormData()
    data.append("file", image)
    data.append("upload_preset","insta-clone")
    data.append("cloud_name","kamleshcloud")
    try {
      const response = await fetch("https://api.cloudinary.com/v1_1/kamleshcloud/image/upload",
      {
        method:'POST',
        body:data
      })
      const responseData = await response.json()
      setUrl(responseData.url)
    }catch(err) {
      console.log(err)
    }
  }

  return (
    <div className='createPost'>
      <div className="post-header">
        <h4>Create New Post</h4>
        <button id='post-btn' onClick={() => {postDetails()}}>Share</button>
      </div>
      <div className="main-div">
        <img id="output" src='data:image/png;base64,'/>
        <input type="file" accept="image/*" 
          onChange={(event)=> {
            loadFile(event) 
            setImage(event.target.files[0])
          }}
        />
      </div>
      <div className="details">
        <div className="card-header">
            <div className="card-pic">
                <img src="https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8cGVyc29ufGVufDB8MXwwfHx8MA%3D%3D" alt="" />
            </div>
            <h5>Kamlesh Chandel</h5>
        </div>
        <textarea value={body} onChange={(e) => {setBody(e.target.value)}} placeholder='Write a caption...'></textarea>
      </div>
    </div>
  )
}
