import React,{useEffect, useState} from 'react'
import { useNavigate } from 'react-router-dom'
import './Home.css'

export default function Home() {
  const [data, setData] = useState([])
  const navigate = useNavigate()

  useEffect(()=> {
    const jwt_token = localStorage.getItem("token")
    if(!jwt_token){
      navigate("./signup")
    }

    const fetchData = async () => {
      const token = localStorage.getItem("token")
      try{const res = await fetch("http://localhost:3005/allPosts",{
        method:"GET",
        headers: {
          "Authorization": `Bearer ${token}`
        }
      })
      const data = await res.json()
      setData(data)
    }catch(err){
      console.log(err)
    }
    }
    fetchData()
  
  },[])

  const likePost = async (id)=> {
    const response = await fetch("http://localhost:3005/like",{
      method:'PUT',
      headers:{
        'Content-Type':"application/json",
        Authorization: "Bearer " + localStorage.getItem("token")
      },
      body: JSON.stringify({
        postId : id
      })
    })
    const data = await response.json()
  }

  const unlikePost = async (id)=> {
    const response = await fetch("http://localhost:3005/unlike",{
      method:'PUT',
      headers:{
        'Content-Type':"application/json",
        Authorization: "Bearer " + localStorage.getItem("token")
      },
      body: JSON.stringify({
        postId : id
      })
    })
    const data = await response.json()
  }

  return (
    <div className="home">
      {
        data.map((eachPost) => {
          return (
            <div className="cardd">
            <div className="card-header">
              <div className="card-pic">
                <img src="https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8cGVyc29ufGVufDB8MXwwfHx8MA%3D%3D" alt="profile" />
              </div>
              <h5>{eachPost.postedBy.name}</h5>
            </div>
            <div className="card-image">
              <img src={eachPost.photo} alt="photo" />
            </div>
            <div className="card-content">
              <div className="spanandlike">
              <span class="material-symbols-outlined" onClick={() => {likePost(eachPost._id)}}>
                favorite
              </span>
              <span class="material-symbols-outlined material-symbols-outlined-red" onClick={() => {unlikePost(eachPost._id)}}>
                favorite
              </span>
              <p>1 Like</p>
              </div>
            <p>{eachPost.body}</p>
            <div className="add-comment">
            <span class="material-symbols-outlined">
               mood
            </span>
            <input type="text" placeholder='Add a comment' />
            <button className="comment">post</button>
            </div>
            </div>
          </div>
          )
        })
      }
      </div>
  )
}
