import React,{useState, useEffect} from 'react'
import PostDetails from './PostDetails'
import {useParams} from 'react-router-dom'
import './Profile.css'

export default function UserProfile() {
  const {userid} = useParams()
  const [user, setUser] = useState("")
  const [show, setShow] = useState(false)
  const [isFollow, setIsFollow] = useState(false)
  const [posts, setPosts] = useState([])
  
  useEffect(()=> {
    const fetchData = async () => {
      try{
        const response = await fetch(`http://localhost:3005/user/${userid}`,{
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token")
        }
        })
        const responseData = await response.json()
        setUser(responseData.user)
        setPosts(responseData.userPost)
        if (
          responseData.user.followers.includes(
            JSON.parse(localStorage.getItem("user"))._id
          )
        ) {
          setIsFollow(true);
        }
      } catch(err){
        console.log(err)
      }
    }
    fetchData()
  },[isFollow])

  const followUser = async userId => {
    const response = await fetch("http://localhost:3005/follow",{
      method: 'PUT',
      headers: {
        'Content-Type':'application/json',
        Authorization: 'Bearer ' + localStorage.getItem("token")
      },
      body: JSON.stringify({
        followId:userId
      })
    })
    const data = await response.json()
    setIsFollow(true)
    console.log(data)
  }

  const unfollowUser = async userId => {
    const response = await fetch("http://localhost:3005/unfollow",{
      method: 'PUT',
      headers: {
        'Content-Type':'application/json',
        Authorization: 'Bearer ' + localStorage.getItem("token")
      },
      body: JSON.stringify({
        followId:userId
      })
    })
    const data = await response.json()
    setIsFollow(false)
    console.log(data)
  }

  return (
    <div className='profile'>
    <div className="profile-frame">
      <div className="profile-pic">
        <img src="https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8cGVyc29ufGVufDB8MXwwfHx8MA%3D%3D" alt="" />
      </div>
      <div className="profile-data">
        <div style={{display:'flex', alignItems:'center', justifyContent:'space-between'}}>
          <h3>{user.name}</h3>
          <button className="followBtn"
           onClick={() => {
            if(isFollow){
              unfollowUser(user._id)
            }else{
              followUser(user._id)
            }
          }}
          >
            {isFollow ? "Unfollow" : "Follow"}
          </button>
        </div>
        <div className="profile-info" style={{display:"flex"}}>
              <p>{posts.length} post</p>
              <p>{user.followers.length} Followers</p>
              <p>{user.following.length} Following</p>
        </div>
        </div>
    </div>
    <hr style={{width:"90%",opacity:"0.8",margin:"25px auto"}}/>
    <div className="gallery">
      {
        posts.map((eachDetails)=> {
          return (
            <img src={eachDetails.photo} alt="gallery"
             onClick={()=> {setShow(true)
               setPosts(eachDetails)
             }} 
            key={eachDetails._id}/>
          )
        })
      }
    </div>
    {/* {
      show && (
        <PostDetails item={posts} showDetails={showDetails}/>
      )
    } */}
    </div>
  )
}
