import React,{useState, useEffect} from 'react'
import PostDetails from './PostDetails'
import {useParams} from 'react-router-dom'
import './Profile.css'

export default function UserProfile() {
    const {userid} = useParams()
  const [userGallery, setUser] = useState([])
  const [show, setShow] = useState(false)
  const [posts, setPosts] = useState([])
  
//   const showDetails = () => {
//     if(show){

//       setShow(false)
//     }else{
//       setShow(true)
//     }
//   }

//   useEffect(()=> {
//     const fetchData = async () => {
//       try{const response = await fetch(`http://localhost:3005/user:${id}`,{
//         headers: {
//           Authorization: "Bearer " + localStorage.getItem("token")
//         }
//       })
//       const responseData = await response.json()
//       setUser(responseData)
//       } catch(err){
//         console.log(err)
//       }
//     }

//     fetchData()
//   },[])

  return (
    <div className='profile'>
    <div className="profile-frame">
      <div className="profile-pic">
        <img src="https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8cGVyc29ufGVufDB8MXwwfHx8MA%3D%3D" alt="" />
      </div>
      <div className="profile-data">
        <h3>{JSON.parse(localStorage.getItem("user")).name}</h3>
        <div className="profile-info" style={{display:"flex"}}>
              <p>1 post</p>
              <p>1 followers</p>
              <p>1 followings</p>
        </div>
        </div>
    </div>
    <hr style={{width:"90%",opacity:"0.8",margin:"25px auto"}}/>
    <div className="gallery">
      {
        userGallery.map((eachDetails)=> {
          return (
            <img src={eachDetails.photo} alt="gallery"
             onClick={()=> {setShow(true)
               setPosts(eachDetails)
             }} 
            key={eachDetails._id}/>
          )
        })
      }
    </div>
    {/* {
      show && (
        <PostDetails item={posts} showDetails={showDetails}/>
      )
    } */}
    </div>
  )
}
